import Layout from '../components/Layout'
import Toggle from '../components/Toggle'
import randomWords from 'random-words';
import Router from 'next/router'
import { useState } from 'react';
import NewGameButton from '../components/NewGameButton'

export default function Index() {

  const [officialWords, setOfficialWords] = useState(true);

  return (
    <Layout>
      <main class="home">
        <div className="hero">CODENAMES</div>
        <div className="toggle">
          <Toggle text={'Use official words'} onToggle={() => setOfficialWords(!officialWords)} checked={officialWords} />
        </div>
        <NewGameButton includeStyling />
        <div class="mb-4">
          <label class="block text-gray-700 text-sm font-bold mb-2" for="username">
            Current game token
          </label>
          <input class="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline" id="username" type="text" placeholder="sample-token-123" />
        </div>
        <img src='/talkbubble.png' alt='box-art' />
      </main>
      <style jsx global>{`
            @font-face {
              font-family: 'futura', Open Sans', sans-serif;
              src: url('https://fonts.googleapis.com/css?family=Open+Sans&display=swap');
              font-style: normal;
              font-display: swap;
            }
            .toggle {
              width: 214px;
              margin-bottom: 20px;
            }
            .home {
              display: flex;
              flex-direction: column;
              align-items: center;
              border-bottom: solid 10px white;
            }
            .hero {
              width: 100%;
              padding: 20px;
              font-size: 12vw;
              color: white;
              display: flex;
              justify-content: center;
              font-family: futura;
            }
            img {
              margin-top: 30px;
              width: 60%;
              max-width: 513px;
              max-height: 548px;
              justify-self: end;
            }
          `}
      </style>
    </Layout>
  );
}
