import { useState } from 'react';

export default function Card({ card, isCodeMaster, rowIndex, columnIndex, revealCard }) {

  const [clicked, updateClicked] = useState(false);

  const { isRevealed } = card;
  const { team } = card;
  let cardImage = '/card.png';
  console.log('isCodeMaster: ', isCodeMaster);
  if (isCodeMaster || isRevealed || clicked) {
    switch (team) {
      case 0:
        cardImage = '/card.png';
        break;
      case 1:
        cardImage = '/redcard.png'
        break;
      case 2:
        cardImage = '/bluecard.png'
        break;
      case 3:
        cardImage = '/deathcard.png'
        break;
      default:
        cardImage = '/card.png'
    }
  }
  return (
    <main onClick={() => {
      if (!card.isRevealed) {
        revealCard(rowIndex, columnIndex)
        updateClicked(true);
      }
    }}>
      <div className="word" >{card.word}</div>
      <style jsx>{`
        main {
          background-image: url(${cardImage});
          background-size: cover;
          background-repeat: no-repeat;
          display: flex;
          flex-basis: 20%;
          text-align: center;
          align-content: center;
          justify-content: center;
          width: 100%;
          margin: 0 2px;
        }
        .word {
          display: ${isRevealed || clicked ? 'none' : 'flex'};
          align-self: center;
          justify-content: center;
          position: absolute;
          padding-top: 4%;
          font-size: 10px;
          font-family: sans-serif;
          color: #333435;
        }
        .word:hover {
          display: ${isRevealed || clicked ? 'flex' : 'none'};
        }
        main:hover {
          display: flex;
          opacity: ${isRevealed || clicked ? '0.5' : 1};
        }
        @media screen and (min-width: 480px) {
          .word {
          font - size: 15px;
          }
        }
      `}</style>
    </main >
  )
}
