import useSWR from 'swr';
import { useRouter } from 'next/router';
import { useState } from 'react';
import Board from './Board';
import Toggle from '../components/Toggle'

function fetcher(url) {
  return fetch(url).then(r => r.json());
}

export default function Index() {
  const { query } = useRouter();
  const { token } = query;
  const { data, error } = useSWR(`/api/games?token=${token}`, fetcher);

  const [isCodeMaster, setIsCodeMaster] = useState(false);
  const [boardMap, updateBoardMap] = useState(boardMap)

  const revealCard = (x, y) => {
    console.log('revealing: ', x, y);
    // update boardmap for card[x][y] - isRevealed = true;
    boardMap[x][y].isRevealed = true;
  }

  return (
    <main>
      {loading && "Loading..."}
      {boardMap && (<>
        <Toggle text='Codemaster' onToggle={() => setIsCodeMaster(!isCodeMaster)} checked={isCodeMaster} />
        <div>URL: {`${token}`}</div>
        <Board boardMap={boardMap} isCodeMaster={isCodeMaster} revealCard={revealCard} />
      </>)}
    </main>
  );
}
