import { gameStatusToString } from '../constants';

export default function ScoreBoard({ cardsRemaining, scoreBoard }) {
  console.log('cardsRemaining: ', cardsRemaining);
  console.log('scoreBoard: ', scoreBoard);
  const { gameStatus } = scoreBoard;
  return (
    <div className="score-board">
      <div className="team">
        <div className="arrow">
          ""
        </div>
        <div className="color-box red">
          {`${cardsRemaining.red}`}
        </div>
        <div className="cards-remaining">
          CARDS REMAINING
        </div>
      </div>
      <div className="team">
        <div className="arrow">
          `${'>'}`
        </div>
        <div className="color-box blue">
          {`${cardsRemaining.blue}`}
        </div>
        <div className="cards-remaining">
          CARDS REMAINING
        </div>
      </div>
      <style>{`
        .score-board {
          display: flex;
          flex-direction: column;
          width: 450px;
          align-self: center;
        }
        .team {
          display: flex;
          flex-direction: row;
        }
        .arrow {
          flex-basis: 20%;
        }
        .color-box {
          flex-basis: 20%;
        }
        .cards-remaining {
          flex-basis: 60%;
        }
      `}</style>
    </div >
  )
}
