export default function Card({ card, isCodeMaster }) {
  const { isRevealed } = card;
  const { team } = card;
  let cardImage = '/card.png';
  if (isCodeMaster || card.isRevealed) {
    switch (team) {
      case 0:
        cardImage = '/card.png';
        break;
      case 1:
        cardImage = '/redcard.png'
        break;
      case 2:
        cardImage = '/bluecard.png'
        break;
      case 3:
        cardImage = '/deathcard.png'
        break;
      default:
        cardImage = '/card.png'
    }
  }
  // cardImage = '/redcard.png'
  const cardStyle = {
    background: `url(${cardImage})`, backgroundSize: 'cover', backgroundRepeat: 'no-repeat'
  };
  return (
    <main style={cardStyle}>
      <div className="word" >{card.word}</div>
      <style jsx>{`
        main {
          display: flex;
          flex-basis: 20%;
          text-align: center;
          align-content: center;
          justify-content: center;
          width: 100%;
          margin: 0 2px;
        }
        .word {
          display: flex;
          align-self: center;
          justify-content: center;
          position: absolute;
          padding-top: 4%;
          font-size: 10px;
          font-family: sans-serif;
          color: ${team === 0 ? '#333435' : 'white'};
        }
        @media screen and (min-width: 480px) {
          .word {
            font-size: 15px;
          }
        }
      `}</style>
    </main >
  )
}
