import useSWR from 'swr';
import fetch from 'isomorphic-unfetch'
import { useRouter } from 'next/router';
import { useState } from 'react';
import Board from './Board';
import Toggle from '../components/Toggle'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'

function fetcher(url) {
  return fetch(url).then(r => r.json());
}

export default function Index() {
  const { query } = useRouter();
  const { token } = query;
  const { data, error } = useSWR(`/api/games?token=${token}`, fetcher, { refreshInterval: 100 });
  // The following line has optional chaining, added in Next.js v9.1.5,
  // is the same as `data && data.author`
  let boardMap = data?.boardMap;
  const loading = !boardMap;
  if (error) boardMap = 'Failed to fetch game data.';

  const [isCodeMaster, setIsCodeMaster] = useState(false);

  const revealCard = (x, y) => {
    console.log('revealing: ', x, y);
    fetch('/api/games', {
      method: 'PUT',
      headers: {
        'Content-Type': 'application/json'
      },
      body: JSON.stringify({ token, x, y }),
    })
  }

  const copyToClipboard = () => {
    const el = document.createElement('textarea');
    el.value = document.location.href;
    document.body.appendChild(el);
    el.select();
    document.execCommand('copy');
    document.body.removeChild(el);
  };

  return (
    <main>
      {loading && "Loading..."}
      {boardMap && (<>
        <Toggle text='Codemaster' onToggle={() => setIsCodeMaster(!isCodeMaster)} checked={isCodeMaster} />
        <div className="url-holder">



          <button className="btn btn-blue" onClick={() => copyToClipboard()}>
            <FontAwesomeIcon icon="fas fa-copy" />
            <div className="copy-link">Copy Link:</div>
          </button>
          <div className="url">{`${document.location.href}`}</div>
        </div>
        <Board boardMap={boardMap} isCodeMaster={isCodeMaster} revealCard={revealCard} />
      </>)}
      <style>{`
        main {
          width: 100%;
          display: flex;
          flex-direction: column;
          justify-content: center;
        }
        .url-holder{
          align-self: center;
          padding: 5px;
          display: flex;
          justify-content: center;
          padding: 5px;
          border: solid 1px lightgray;
          border-radius: 4px;
          margin-bottom: 10px;
        }
        .url {
          align-self: center;
          margin-left: 5px;
        }
      `}</style>
    </main>
  );
}
