import randomWords from 'random-words';

export default function NewGameButton() {
  const newGame = ({ firstPlayer = null, officialWords = true }) => {
    const randomInt = (min, max) => {
      return Math.round(min + (Math.random() * (max - min)));
    };
    const token = `${randomWords()}-${randomWords()}-${randomInt(100, 999)}`;
    fetch('/api/games', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json'
      },
      body: JSON.stringify({
        token,
        officialWords,
      }),
    })
      .then(res => {
        res.json()
      })
      .then((data) => data)
      .then(() => Router.push(`/game?token=${token}`))
      .catch(err => console.error('Error adding new game'))
  }
  return (
    <button className="btn btn-blue" onClick={newGame}>
      New Game
    </button>
  )
}
