import randomWords from 'random-words'
import fetch from 'isomorphic-unfetch'
import Router from 'next/router'

export default function NewGameButton({ includeStyling, officialWords = true }) {
  const newGame = ({ firstPlayer = null }) => {
    const randomInt = (min, max) => {
      return Math.round(min + (Math.random() * (max - min)));
    };
    const token = `${randomWords()}-${randomWords()}-${randomInt(100, 999)}`;
    const body = JSON.stringify({
      token,
      officialWords,
    })
    fetch('/api/games', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json'
      },
      body,
    })
      .then(() => Router.push(`/game?token=${token}`))
      .catch(err => console.log('Error adding new game: ', err))
      .finally(() => 'Done trying to get new game')
  }
  return (
    <button className={includeStyling ? "btn btn-blue" : "bg-transparent hover:bg-gray-200 text-white-700 font-semibold hover:text-white py-2 px-4 border border-blue-500 hover:border-transparent rounded"} onClick={newGame}>
      New Game
    </button>
  )
}
