import Layout from '../components/Layout'
import Toggle from '../components/Toggle'
import randomWords from 'random-words';
import Router from 'next/router'
import { useState } from 'react';
import NewGameButton from '../components/NewGameButton'

export default function Index() {

  const [officialWords, setOfficialWords] = useState(true);

  return (
    <Layout>
      <main className="self-center">
        <Toggle text={'Use official words'} onToggle={() => setOfficialWords(!officialWords)} checked={officialWords} />
        <div className="hero">CODENAMES</div>
        <div className="new-game">
          <NewGameButton />
        </div>
        <style jsx global>{`
            @font-face {
              font-family: 'Open Sans', sans-serif;
              src: url('https://fonts.googleapis.com/css?family=Open+Sans&display=swap');
              font-weight: bold;
              font-style: normal;
              font-display: swap;
            }
            main {
              display: flex;
              flex-direction: column;
            }
            .hero {
              width: 100%;
              padding: 20px;
              font-size: 5em;
            }
            .new-game {
              align-self: center;
              width: 200px;
              display: flex;
              justify-content: center;
            }
          `}
        </style>
      </main>
    </Layout>
  );
}
