import nextConnect from 'next-connect';
import middleware from '../../database';
import randomWords from 'random-words';

const handler = nextConnect();

handler.use(middleware);

handler.get(async (req, res) => {
  const { token } = req.query;
  try {
    const game = await req.db.collection('games').findOne({ token });
    res.json(game);
  } catch (err) {
    throw new Error(`Error loadin game with token: ${token}: ${err}`);
  }
});

handler.post(async (req, res) => {

  // const randomInt = (min, max) => {
  //   return Math.round(min + (Math.random() * (max - min)));
  // };
  // const token = `${randomWords()}${randomWords()}${randomInt(100, 999)}`;
  const firstPlayer = req.query.firstPlayer || Math.random() >= 0.5 ? 1 : 2;
  const boardMap = [];
  for (let i = 0; i < 5; i++) {
    const row = [];
    for (let j = 0; j < 5; j++) {
      row.push({ word: randomWords(), team: 0, isRevealed: false });
    }
    boardMap.push(row);
  }
  const addCardOwner = (int) => {
    let x = randomInt(0, 4);
    let y = randomInt(0, 4);
    while (boardMap[x][y].team !== 0) {
      x = randomInt(0, 4);
      y = randomInt(0, 4);
    }
    boardMap[x][y].team = int;
  }
  for (let i = 0; i < 9; i++) {
    addCardOwner(firstPlayer === 1 ? 1 : 2);
    if (i !== 0) {
      addCardOwner(firstPlayer === 1 ? 2 : 1);
    }
  }
  addCardOwner(3);
  const game = {
    token,
    boardMap,
    players: {
      blue: [],
      red: [],
    },
  };
  try {
    const result = await req.db.collection('games').insertOne(game);
    console.log(`New game created with the following id: ${result.insertedId} `);
    res.json(token);
  } catch (err) {
    throw new Error(`Error adding new game: ${err}`)
  }
});

export default handler;
