import Card from './Card'

export default function Row({ cards }) {
  const cardDivs = cards.map((card, index) => (
    <Card word={card.word} key={index} />
  ))

  return (
    <div>
      {cardDivs}
      <style jsx>{`
        .row {
          display: flex;
          flex-basis: 100%;
        }
        .quote {
          font-family: cursive;
          color: #e243de;
          font-size: 24px;
          padding-bottom: 10px;
        }
        .author {
          font-family: sans-serif;
          color: #559834;
          font-size: 20px;
        }
      `}</style>
    </div>
  )
}
