import Layout from '../components/Layout'
import Toggle from '../components/Toggle'
import randomWords from 'random-words';
import Router from 'next/router'
import { useState } from 'react'; 

export default function Index() {

  const [firstPlayer, setFirstPlayer] = useState(null);

  const newGame = ({ firstPlayer = null, regulationWords = true }) => {
    const randomInt = (min, max) => {
      return Math.round(min + (Math.random() * (max - min)));
    };
    const token = `${randomWords()}-${randomWords()}-${randomInt(100, 999)}`;
    fetch('/api/games', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json'
      },
      body: JSON.stringify({
        token,
        firstPlayer,
        officialWords,
      }),
    })
      .then(res => {
        console.log('response: ', res);
        res.json()
      })
      .then((data) => data)
      .catch(err => console.error('Error adding new game'))
    Router.push(`/game?token=${token}`)
  }
  return (
    <Layout>
      <main className="center">
        <Toggle text={'Use official words'} active={firstPlayer==='red'} />
        <button className="btn btn-blue" onClick={newGame}>
          New Game 
        </button>
        <div className="center">HomePage What</div>
        {/* {boardMap && <span className="author">- {boardMap}</span>} */}

        <style jsx global>{`
            @font-face {
              font-family: 'Open Sans', sans-serif;
              src: url('https://fonts.googleapis.com/css?family=Open+Sans&display=swap');
              font-weight: bold;
              font-style: normal;
              font-display: swap;
            }`

        }
        </style>
      </main>
    </Layout>
  );
}
