import useSWR from 'swr';
import { useRouter } from 'next/router';
import Board from './Board'

function fetcher(url) {
  return fetch(url).then(r => r.json());
}

export default function Index() {
  const { query } = useRouter();
  const { token } = query;
  const { data, error } = useSWR(`/api/games?token=${token}`, fetcher);
  // The following line has optional chaining, added in Next.js v9.1.5,
  // is the same as `data && data.author`
  console.log('data: ', data);
  let boardMap = data?.boardMap;

  if (!boardMap) boardMap = 'Loading...';
  if (error) boardMap = 'Failed to fetch game data.';

  return (
    <main className="center">
      <div className="quote">{JSON.stringify(boardMap)}</div>
      {/* {boardMap && <span className="author">- {boardMap}</span>} */}

      <style jsx>{`
        main {
          width: 90%;
          max-width: 900px;
          margin: 300px auto;
          text-align: center;
        }
        .quote {
          font-family: cursive;
          color: #e243de;
          font-size: 24px;
          padding-bottom: 10px;
        }
        .author {
          font-family: sans-serif;
          color: #559834;
          font-size: 20px;
        }
      `}</style>
    </main>
  );
}
