const randomWords = require('random-words');
import fetch from 'isomorphic-unfetch'
import Router from 'next/router'

export default function NewGameButton({ includeStyling }) {
  const newGame = ({ firstPlayer = null, officialWords = true }) => {
    const randomInt = (min, max) => {
      return Math.round(min + (Math.random() * (max - min)));
    };
    const token = `${randomWords()}-${randomWords()}-${randomInt(100, 999)}`;
    const body = JSON.stringify({
      token,
      officialWords,
    })
    fetch('/api/games', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json'
      },
      body,
    })
      .then(() => Router.push(`/game?token=${token}`))
      .catch(err => console.log('Error adding new game: ', err))
      .finally(() => 'Done trying to get new game')
  }
  return (
    <button className={"btn btn-blue"} onClick={newGame}>
      New Game
    </button>
  )
}
