import Card from './Card'

export default function Row({ cards }) {
  const cardDivs = cards.map((card, index) => (
    <Card word={card.word} key={index} />
  ))

  return (
    <main>
      {cardDivs}
      <style jsx>{`
        main {
          display: flex;
          flex-direction: row;
          justify-content: center;
          flex-basis: 100%;
          margin: 4px;
        }
      `}</style>
    </main>
  )
}
