import Card from './Card'

export default function Row({ cards }) {
  const cardDivs = cards.map((card, index) => (
    <Card word={card.word} key={index} />
  ))

  return (
    <div>
      <div className="row">
        {cardDivs}
      </div>
      <style jsx>{`
        .row {
          display: flex;
          flex-direction: row;
          justify-content: center;
          height: 100%;
        }
      `}</style>
    </div>
  )
}
