export default function ScoreBoard({ cardsRemaining, scoreBoard }) {


  return (
    <div className="scoreBoard">
      <div className="turn">
        {!scoreBoard.gameOver
          && cardsRemaining.red > 0
          && cardsRemaining.blue > 0
          && `${scoreBoard?.turn === 1 ? "Red's turn" : "Blue's turn"}`}
        {scoreBoard.gameOver && "Game Over!"}
        {cardsRemaining.red === 0 && "Red wins!"}
        {cardsRemaining.blue === 0 && "Blue wins!"}
      </div>
      <div className="score">
        Blue Cards Remaining: {`${cardsRemaining.blue}`}
      </div>
      <div className="score">
        Red Cards Remaining: {`${cardsRemaining.red}`}
      </div>
    </div>
  )
}
