// import cardImage from '../public/images/'
// import style from 'next/css'

export default function Card({ word, isCodeMaster }) {
  const plainCard = '/card.png';
  const cardStyle = {
    background: `url(${'/card.png'})`, backgroundSize: 'cover', backgroundRepeat: 'no-repeat'
  };
  return (
    <main style={cardStyle} >
      <div className="word" >{word}</div>
      <style jsx>{`
        main {
          display: flex;
          flex-basis: 20%;
          text-align: center;
          align-content: center;
          justify-content: center;
          width: 100%;
          margin: 0 2px;
        }
        .word {
          display: flex;
          align-self: center;
          justify-content: center;
          position: absolute;
          padding-top: 4%;
          font-size: 10px;
          font-family: sans-serif;
        }
        @media screen and (min-width: 480px) {
          .word {
            font-size: 15px;
          }
        }
      `}</style>
    </main >
  )
}
