import Row from './Row';

export default function Board({ boardMap }) {
  console.log('boardMap: ', boardMap);
  const cardDivs = boardMap.map((row, index) => (
    <Row cards={row} key={index} />
  ));

  return (
    <div>
      <div>{cardDivs}</div>
      <style jsx>{`
        main {
          display: flex;

          width: 90%;
          max-width: 900px;
          margin: 300px auto;
          text-align: center;
        }
        .quote {
          font-family: cursive;
          color: #e243de;
          font-size: 24px;
          padding-bottom: 10px;
        }
        .author {
          font-family: sans-serif;
          color: #559834;
          font-size: 20px;
        }
      `}</style>
    </div>
  )
}
