export default function Toggle({ text, onToggle, active }) {
  const input = <input id="toogleA" type="checkbox" className="hidden" />
  return (
    <div className="flex items-center justify-center w-full mb-24"
      onClick={onToggle}
    >
      <label
        for="toogleA"
        className="flex items-center cursor-pointer"
      >
        <div className="relative">
          (
            <input id="toogleA" type="checkbox" className="hidden" />
          )
          <div
            className="toggle__line w-10 h-4 bg-gray-400 rounded-full shadow-inner"
          />
          <div
            className="toggle__dot absolute w-6 h-6 bg-white rounded-full shadow inset-y-0 left-0"
          />
        </div>
        <div
          className="text ml-3 font-medium"
          id="toggleText"
        >
          {text}
        </div>
      </label>
      <style>{`
        .toggle__dot {
          top: -.25rem;
          left: -.25rem;
          transition: all 0.3s ease-in-out;
        }
        input:checked ~ .toggle__dot {
          transform: translateX(100%);
          background-color: #48bb78;
        }
        input:checked ~ #toggleText {
          transform: translateX(100%);
          color: blue;
        }
      `}</style>
    </div>
  )
}
