import { gameStatusToString } from '../constants';

export default function ScoreBoard({ cardsRemaining, scoreBoard }) {
  console.log('cardsRemaining: ', cardsRemaining);
  console.log('scoreBoard: ', scoreBoard);
  const { gameStatus } = scoreBoard;
  return (
    <div className="scoreBoard">
      <div className="turn">
        {`${gameStatusToString[gameStatus]}`}
      </div>
      <div className="score">
        Blue Cards Remaining: {`${cardsRemaining.blue}`}
      </div>
      <div className="score">
        Red Cards Remaining: {`${cardsRemaining.red}`}
      </div>
      <style>{`
        .scoreBoard {
          display: flex;
          flex-difrection: row wrap;
        }

      `}</style>
    </div>
  )
}
