import { useState } from 'react';

export default function Card({ card, isCodeMaster, rowIndex, columnIndex, revealCard }) {

  const [clicked, updateClicked] = useState(false);
  const [hover, setHover] = useState(false);

  const { isRevealed } = card;
  const { team } = card;
  let cardImage = '/card.png';
  if (isCodeMaster) {
    switch (team) {
      case 0:
        cardImage = '/card.png';
        break;
      case 1:
        cardImage = '/redtint.png'
        break;
      case 2:
        cardImage = '/bluetint.png'
        break;
      case 3:
        cardImage = '/deathtint.png'
        break;
      default:
        cardImage = '/card.png'
    }
  }
  if (isRevealed || clicked) {
    switch (team) {
      case 0:
        cardImage = '/neutral.png';
        break;
      case 1:
        cardImage = '/red.png'
        break;
      case 2:
        cardImage = '/blue.png'
        break;
      case 3:
        cardImage = '/death.png'
        break;
      default:
        cardImage = '/card.png'
    }
  }
  return (
    <main
      onClick={() => {
        if (!card.isRevealed) {
          revealCard(rowIndex, columnIndex)
          updateClicked(true);
        }
      }}
      onMouseEnter={() => setHover(true)}
      onMouseLeave={() => setHover(false)}
    >
      <div className="word" >{card.word}</div>
      <style jsx>{`
        main {
          background-image: url(${cardImage});
          background-size: cover;
          background-repeat: no-repeat;
          display: flex;
          flex-basis: 20%;
          text-align: center;
          align-content: center;
          justify-content: center;
          width: 100%;
          margin: 0 2px;
        }
        .word {
          align-self: center;
          justify-content: center;
          position: absolute;
          padding-top: 4%;
          font-size: 10px;
          font-family: sans-serif;
          font-weight: 500;
          color: ${(isRevealed || clicked) && !hover ? 'rgba(0, 0, 0, 0.0)' : 'black'};
        }
        .word:hover {
          color: '#333435';
        }
        main:hover {
          display: flex;
          opacity: ${isRevealed || clicked ? '0.5' : 1};
        }
        @media screen and (min-width: 480px) {
          .word {
          font-size: 15px;
          }
        }
      `}</style>
    </main >
  )
}
